import { NavLink } from "react-router-dom"
import i18n from "i18next";
import { initReactI18next, useTranslation } from 'react-i18next'
i18n
    .use(initReactI18next)
    .init({
        resources: {
            en: {
                translation: {
                    "home": "home"
                }
            },
            vi: {
                translation: {
                    "home": "Trang Chủ"
                }
            }
        },
        lng: "vi",
        fallbackLng: "vi",

        interpolation: {
            escapeValue: false
        }
    });
const LinkList: { id: number; to: string; title: string }[] = [
    {
        id: 1,
        to: "/",
        title: "Home",
    },
    {
        id: 2,
        to: "/contact",
        title: "Contact",
    },
    {
        id: 3,
        to: "/about",
        title: "About",
    },
    {
        id: 4,
        to: "/signup",
        title: "Sign Up",
    },
];


const ListNav = () => {
    const { t } = useTranslation();
    return (
        <div className='flex gap-x-10 font-normal leading-6 translate-x-24'>
            {LinkList.map((item: { id: number; to: string; title: string }, index: number) => (
                <NavLink
                    to={item.to}
                    key={item.id}
                    className={({ isActive }) =>
                        isActive
                            ? "border border-b-[#534949] border-t-transparent border-r-transparent border-l-transparent pb-2 transition-all"
                            : ""
                    }>
                    {t(item.title)}
                </NavLink>
            ))}
        </div>
    );
};

export default ListNav;
