import React, { useState, useCallback } from "react";

interface Option {
    value: string;
    label: string;
    flag: string;
}

const LanguageDropdown: React.FC = () => {
    const options: Option[] = [
        { value: "vn", label: "Việt Nam", flag: "https://antimatter.vn/wp-content/uploads/2022/12/hinh-nen-co-viet-nam.jpg" },
        { value: "en", label: "England", flag: "https://vuongquocanh.com/wp-content/uploads/2018/04/la-co-vuong-quoc-anh.jpg" }
    ];
    const [isOpen, setIsOpen] = useState(false);
    const [selectedOption, setSelectedOption] = useState<Option>(options[0]);

    const toggleDropdown = useCallback(() => {
        setIsOpen(prevIsOpen => !prevIsOpen);
    }, []);

    const handleOptionClick = useCallback((option: Option) => {
        setSelectedOption(option);
        setIsOpen(false);
    }, []);

    return (
        <div className="dropdown">
            <div className="dropdown-toggle" onClick={toggleDropdown}>
                <img src={selectedOption.flag} alt={selectedOption.label} width="30" />
            </div>
            {isOpen && (
                <div className="dropdown-menu">
                    {options.map(option => (
                        <div
                            key={option.value}
                            className="dropdown-item"
                        // onClick={() => handleOptionClick(option)}    
                        >
                            <img src={option.flag} alt={option.label} width="30" />
                        </div>
                    ))}
                    <div>  <img src={options[0].flag} alt={option.label} width="30" /></div>
                </div>
            )}
        </div>
    );
};

export default LanguageDropdown;
