    const { t } = useTranslation();
    const [on, setOn] = useState<Boolean>(false);
    const [select, setSelect] = useState<string>('');
    const options = ['English'] 
    const handleClick = () => {
        setOn(!on)
    }

    const changeLanguage = (lng: 'en' | 'vi') => {
        i18n.changeLanguage(lng)
    }
