import i18n from "i18next";
import { initReactI18next } from "react-i18next";

const resources = {
  en: {
    translation: {
      "home": "Home",
      "contact":"Contact",
      "about":"About",
      "signup":"SignUp",
      "title": "Summer Sale For All Swim Suits And Free Express Delivery - OFF 50%!",
      "shopnow":"ShopNow",
      "create account":"Create an account",
      'info':"Enter your details below"
    }
  },
  vi: {
    translation: {
      "home": "Trang Chủ",
      "contact": "Liên Hệ",
      "about": "Xem Thêm",
      "signup": "Đăng Kí",
      "title":"Giảm giá mùa hè cho tất cả các bộ đồ bơi và chuyển phát nhanh miễn phí - GIẢM GIÁ 50%!",
      "shopnow":"Mua ngay",
      "create account":"Tạo tài khoản",
      'info':"Nhập thông tin chi tiết của bạn  đây"
    }
  }
};

i18n
  .use(initReactI18next) 
  .init({
    resources,
    lng: "vi", 

    interpolation: {
      escapeValue: false 
    }
  });

  export default i18n;
