import React, { useState } from "react";
import { Dropdown, ButtonGroup } from "react-bootstrap";
import i18n from "@i18n";
const options = [
    {
        value: "en",
        label: (
            <img src="https://vuongquocanh.com/wp-content/uploads/2018/04/la-co-vuong-quoc-anh.jpg" width="20" alt="" />
        )
    },
    {
        value: "vi",
        label: (
            <img src="https://antimatter.vn/wp-content/uploads/2022/12/hinh-nen-co-viet-nam.jpg" width="20" alt="" />
        )
    } //de
];
const SelectLanguage = () => {

    const [lang, setLang] = useState("en");
    console.log(lang)
    const [langLabel, setLangLabel] = useState(options[0].label);

    function handlclick(n: any) {
        setLangLabel(options[n].label);
        setLang(options[n].value);
    }
    const changeLanguage = (lng: 'en' | 'vi') => {
        i18n.changeLanguage(lng)
    }
    return (
        <div className="App">
            <Dropdown as={ButtonGroup}>
                <Dropdown.Toggle
                    className=" bg-transparent btn-outline-secondary border-0 "
                    id="lng-dropdown"
                >
                    {langLabel}
                </Dropdown.Toggle>
                <Dropdown.Menu className="bg-black text-white">
                    <Dropdown.Item onClick={() => handlclick(0)}>
                        <div onClick={() => changeLanguage('en')}>{options[0].value}</div>
                    </Dropdown.Item>
                    <Dropdown.Item onClick={() => handlclick(1)}>
                        <div onClick={() => changeLanguage('vi')}>{options[1].value}</div>
                    </Dropdown.Item>
                </Dropdown.Menu>
            </Dropdown>
        </div>
    );
}

export default SelectLanguage;
