import "./styles.css";
import React, { useState } from "react";
import { Dropdown, ButtonGroup } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.css";
import ukflag from "./img/uk.png";
import deflag from "./img/de.png";

const SelectLanguage = () => {
    const options = [
        {
            value: "en",
            label: (
                <img src={ukflag} width="20" alt="" />
            )
        },
        {
            value: "de",
            label: (
                <img src="" width="20" alt="" />
            )
        } //de
    ];
    const [lang, setLang] = useState("en");
    console.log(lang)
    const [langLabel, setLangLabel] = useState(options[0].label);

    function handlclick(n: any) {
        setLangLabel(options[n].label);
        setLang(options[n].value);
    }
    return (
        <div className="App">
            <h2>react-languages-select</h2>
            <Dropdown as={ButtonGroup}>
                <Dropdown.Toggle
                    className=" bg-transparent btn-outline-secondary border-0 "
                    id="lng-dropdown"
                >
                    {langLabel}
                </Dropdown.Toggle>
                <Dropdown.Menu>
                    <Dropdown.Item onClick={() => handlclick(0)}>
                        {options[0].value}
                    </Dropdown.Item>
                    <Dropdown.Item onClick={() => handlclick(1)}>
                        {options[1].value}
                    </Dropdown.Item>
                </Dropdown.Menu>
            </Dropdown>
        </div>
    );
}

export default SelectLanguage;
