import React, { useState } from "react";
import { Dropdown, ButtonGroup } from "react-bootstrap";

const SelectLanguage = () => {
    const options = [
        {
            value: "En",
            label: (
                <img
                    src="https://vuongquocanh.com/wp-content/uploads/2018/04/la-co-vuong-quoc-anh.jpg"
                    width="20"
                    alt=""
                />
            ),
        },
        {
            value: "Vi",
            label: (
                <img
                    src="https://upload.wikimedia.org/wikipedia/commons/thumb/1/1b/Flag_of_North_Vietnam_%281955%E2%80%931976%29.svg/230px-Flag_of_North_Vietnam_%281955%E2%80%931976%29.svg.png"
                    width="20"
                    alt=""
                />
            ),
        },
    ];
    const [lang, setLang] = useState(options[0].value);
    const [langLabel, setLangLabel] = useState(options[0].label);

    function handleClick(n: number) {
        setLangLabel(options[n].label);
        setLang(options[n].value);
    }

    return (
        <div className="App">
            <Dropdown as={ButtonGroup}>
                <Dropdown.Toggle
                    className=" bg-transparent btn-outline-secondary border-0 "
                    id="lng-dropdown"
                >
                    <div>{langLabel}</div>
                </Dropdown.Toggle>
                <Dropdown.Menu>
                    {options.map((option, index) => (
                        <Dropdown.Item key={index} onClick={() => handleClick(index)}>
                            {option.label}
                        </Dropdown.Item>
                    ))}
                </Dropdown.Menu>
            </Dropdown>
        </div>
    );
};

export default SelectLanguage;
