import React, { useState } from 'react';
import url from "@assets/image/avt3.png"
import { FiChevronDown, FiChevronUp } from 'react-icons/fi';

const TitleProductCart: React.FC = () => {
    const [count, setCount] = useState(0)
    const incrementHandle = () => {
        setCount(count => count + 1)
    }
    const decrementHandle = () => {
        setCount(count => count - 1)
    }
    return (
        <>
            <div className='flex w-[1370px] centerAll mx-auto gap-x-64 py-5 shadow-lg mt-20'>
                <span>Product</span>
                <span>Price</span>
                <span>Quantity</span>
                <span>Subtotal</span>
            </div>
            <div className='flex w-[1370px] centerAll mx-auto gap-x-64 py-5 shadow-lg mt-20'>
                <div className='flex gap-x-5 -translate-x-10'>
                    <img src={url} alt="" className='w-[80px]' />
                    <div className='centerAll'>LCD Monitor</div>
                </div>
                <div className='-translate-x-20'>$650</div>
                <div className='-translate-x-20 centerAll gap-x-8 border w-[90px] border-gray-400 rounded-lg px-4 py-2'>
                    <div className='centerAll'>{count}</div>
                    <div className='grid gap-y-2'>
                        <div className='cursor-pointer' onClick={incrementHandle}>
                            <FiChevronUp></FiChevronUp>
                        </div>
                        <div className='cursor-pointer' onClick={decrementHandle}>
                            <FiChevronDown></FiChevronDown>
                        </div>
                    </div>
                </div>
                <div className='-translate-x-20'>$650</div>
            </div>
        </>
    );
};

export default TitleProductCart;
