import React, { useEffect, useRef, useState } from 'react';
import { useTranslation } from 'react-i18next';
import { FiLogOut, FiShoppingBag, FiStar, FiUnlock, FiUser } from 'react-icons/fi';

const listMenuUser: { id: number, icon: any, name: string }[] = [
    {
        id: 1,
        icon: <FiUser></FiUser>,
        name: "listuser1"
    },
    {
        id: 2,
        icon: <FiShoppingBag></FiShoppingBag>,
        name: "listuser2"
    },
    {
        id: 3,
        icon: <FiUnlock></FiUnlock>,
        name: "listuser3"
    },
    {
        id: 4,
        icon: <FiStar></FiStar>,
        name: "listuser4"
    },
    {
        id: 5,
        icon: <FiLogOut></FiLogOut>,
        name: "listuser5"
    },

]
const UserList = () => {
    const { t } = useTranslation();
    const [on,setOn] = useState
    const userRef = useRef<HTMLDivElement>(null);
    useEffect(() => {
        document.addEventListener("click", (e: any) => {
            if (e.target !== userRef.current) {
                setOn(false)
            }
        })
    }, [])
    return (
        <div className='bg-gradient-to-br from-gray-900 via-gray-900 to-gray-700 text-white w-[213px] absolute right-0 top-10 z-10' ref={userRef}>
            {listMenuUser.map((item: any, index: any) => (
                <div key={item.index} className='flex  px-2 py-5 hover:bg-blue-400 gap-x-3'>
                    <div className='setIcon'>{item.icon}</div>
                    <div className='text-[14px]'>{t(item.name)}</div>
                </div>
            ))}
        </div>
    );
};

export default UserList;
