import i18n from "i18next";
import { initReactI18next } from "react-i18next";

const resources = {
  en: {
    translation: {
      "home": "Home"
    }
  },
  vi: {
    translation: {
      "home": "Trang Chủ"
    }
  }
};

i18n
  .use(initReactI18next) 
  .init({
    resources,
    lng: "vi", 

    interpolation: {
      escapeValue: false 
    }
  });

  export default i18n;
