import i18n from "@i18n";
import React, { useState } from "react";
import { Dropdown, ButtonGroup } from "react-bootstrap";
const options = [
    {
        label: (
            <img src="https://vuongquocanh.com/wp-content/uploads/2018/04/la-co-vuong-quoc-anh.jpg" width="20" alt="" />
        )
    },
    {
        label: (
            <img src="https://upload.wikimedia.org/wikipedia/commons/thumb/1/1b/Flag_of_North_Vietnam_%281955%E2%80%931976%29.svg/230px-Flag_of_North_Vietnam_%281955%E2%80%931976%29.svg.png" width="20" alt="" />
        )
    } //de
];

const SelectLanguage = () => {

    const [langLabel, setLangLabel] = useState(options[0].label);
    const SetImage = (n: any) => {
        setLangLabel(options[1].label);
    }
    const DoubleClick = (n: any) => {
        setLangLabel(options[0].label);
    }
    const changeLanguage = (lng: 'en' | 'vi') => {
        i18n.changeLanguage(lng)
    }
    return (
        <div className="App">
            <Dropdown as={ButtonGroup}>
                <Dropdown.Toggle
                    className=" bg-transparent btn-outline-secondary border-0 "
                    id="lng-dropdown"
                >
                    <div onClick={SetImage} onDoubleClick={DoubleClick} >{langLabel}</div>
                </Dropdown.Toggle>
            </Dropdown>
        </div>
    );
}

export default SelectLanguage;
